aggregation/collect.html.body:
turf.collect
Joins attributes FeatureCollection of polygons with a FeatureCollection of points.















    Turf and OpenLayers 3 - Collect




      // Declare a source for points and drawing
      var vectorSourcePolygons = new ol.source.Vector();
      var vectorSourcePoints = new ol.source.Vector();
      var pointsStyle = [
        new ol.style.Style({
          image: new ol.style.Circle({
            stroke: new ol.style.Stroke({
              color: 'white'
            }),
            fill: new ol.style.Fill({
              color: '#1f6b75'
            }),
            radius: 4
          })
        })
      ];
      var vectorLayerPolygons = new ol.layer.Vector({
        source: vectorSourcePolygons,
        style: [
          new ol.style.Style({
            stroke: new ol.style.Stroke({
              color: 'black',
              width: 1
            }),
            fill: new ol.style.Fill({
              color: 'rgba(0, 0, 0, 0)'
            })
          })
        ]
      });
      var vectorLayerPoints = new ol.layer.Vector({
        source: vectorSourcePoints,
        style: pointsStyle
      });
      // Instanciate a map and add layers
      var map = new ol.Map({
        target: 'map',
        layers: [
          new ol.layer.Tile({
            source: new ol.source.OSM()
          }),
          vectorLayerPolygons,
          vectorLayerPoints
        ],
        view: new ol.View({
          center: ol.proj.transform(
            [2.197, 46.498],
            'EPSG:4326',
            'EPSG:3857'
          ),
          zoom: 5
        })
      });
      Promise.all([
        fetch('assets/data/regions-20140528-6digits-100m.geojson').then(function(response) {
          return response.json().then(function(json) {
            return json;
          });
        }),
        fetch('assets/data/france_meteo.geojson').then(function(response) {
          return response.json().then(function(json) {
            return json;
          });
        })
      ]).then(function(returned) {
        var polys_fc = returned[0];
        var points_fc = returned[1];
        var collect = turf.collect(polys_fc, points_fc, 'pressure', 'values');
        collect.features.forEach(function(el) {
          el.properties.average = el.properties.values.reduce(function(sum, a) {
            return sum + a;
          }, 0) / (el.properties.values.length || 1);
          delete el.properties.values;
        })
        console.log(collect);

        vectorSourcePolygons.addFeatures(geojsonToFeatures(collect, {
          featureProjection: 'EPSG:3857'
        }));
        vectorSourcePoints.addFeatures(geojsonToFeatures(points_fc, {
          featureProjection: 'EPSG:3857'
        }));
      });
      var popup = new ol.Overlay.Popup();
      map.addOverlay(popup);
      map.on('click', function(evt) {
        var prettyCoord = ol.coordinate.toStringHDMS(
          ol.proj.transform(evt.coordinate, 'EPSG:3857', 'EPSG:4326'),
          2
        );
        var feature = map.forEachFeatureAtPixel(evt.pixel, function(feature) {
          return feature;
        }, {
          layerFilter: function(layer){
            return layer === vectorLayerPolygons;
          }
          // ,hitTolerance: 1
        });
        var popup_content;
        if (feature) {
          popup_content = 'Name: ' + feature.get('nom') + '' +
                          'Number of municipalities : ' +
                          feature.get('nb_comm') + '' +
                          'Name of the "chef-lieu" : ' +
                          feature.get('nom_cl') + '' +
                          'Average pressure : ' + feature.get('average');
          popup.show(evt.coordinate, 'Coordinates' +
                     popup_content + '');
        } else {
          popup.hide();
        }
      });

measurement/along.html.body:
turf-along
Takes a line and returns a point at a specified distance along the line.









      #length {
        width: 300px;
      }





    Turf and OpenLayers - Along




    Validate

      var geojson;
      var length = document.getElementById('length');
      var button = document.getElementById('validate');
      button.addEventListener('click', function(e) {
        var inputValue = length.value;
        if (inputValue.match(/^\d+$/)) {
          var along = turf.along(geojson.features[0], +inputValue, {
            units: 'kilometers'
          });
          console.log(along);
          var featuresPt = (new ol.format.GeoJSON()).readFeatures(along, {
            dataProjection: 'EPSG:4326',
            featureProjection: 'EPSG:3857'
          });
          vectorLayerPoint.getSource().clear();
          vectorLayerPoint.getSource().addFeatures(featuresPt);
        } else {
          console.log('The input is invalid!');
        }
      });

      // Declare an empty layer for point
      var vectorLayerPoint = new ol.layer.Vector({
        source: new ol.source.Vector()
      });

      // Declare an empty layer for a line
      var vectorLayerLine = new ol.layer.Vector({
        source: new ol.source.Vector(),
        style: [
          new ol.style.Style({
            stroke: new ol.style.Stroke({
              color: 'white',
              width: 5
            })
          }),
          new ol.style.Style({
            stroke: new ol.style.Stroke({
              color: '#007cc3',
              width: 3
            })
          })
        ]
      });

      // Instanciate a map and add layers
      var map = new ol.Map({
        target: 'map',
        layers: [
          new ol.layer.Tile({
            source: new ol.source.OSM()
          }),
          vectorLayerLine,
          vectorLayerPoint
        ],
        view: new ol.View({
          center: ol.proj.transform(
            [3.768, 48.879],
            'EPSG:4326',
            'EPSG:3857'
          ),
          zoom: 6
        })
      });

      fetch('assets/data/from-nantes-to-bruxelles-osrm.geojson').then(function(response) {
        return response.json();
      }).then(function(json) {
        // To expose the geojson content to the outside
        geojson = json;
        // Serialize features from GeoJSON to array of ol.Feature
        var features = (new ol.format.GeoJSON()).readFeatures(json, {
          dataProjection: 'EPSG:4326',
          featureProjection: 'EPSG:3857'
        });
        // Add features to the line layer source
        vectorLayerLine.getSource().addFeatures(features);
      }).catch(function(ex) {
        console.log('parsing failed', ex);
      });

measurement/area.html.body:
turf-area
Calculates the total geographical area in meters squared of any GeoJSON object.













    Turf and OpenLayers 3 - Area





      var surface = document.getElementById('surface');
      var not_selected = 'No surface has been selected!';
      surface.innerHTML = not_selected;
      // Declare a source for polygons
      var vectorSourcePolygons = new ol.source.Vector();

      var vectorLayerPolygons = new ol.layer.Vector({
        source: vectorSourcePolygons,
        style: [
          new ol.style.Style({
            stroke: new ol.style.Stroke({
              color: [0, 121, 88, 1],
              width: 2
            }),
            fill: new ol.style.Fill({
              color: 'rgba(0, 0, 0, 0)'
            })
          })
        ]
      });

      // Instanciate a map and add layers
      var map = new ol.Map({
        target: 'map',
        layers: [
          new ol.layer.Tile({
            source: new ol.source.OSM()
          }),
          vectorLayerPolygons
        ],
        view: new ol.View({
          center: ol.proj.transform(
            [-1.5603, 47.2383],
            'EPSG:4326',
            'EPSG:3857'
          ),
          zoom: 11
        })
      });
      var select = new ol.interaction.Select();
      select.on('select', function(e) {
        var geojson = (new ol.format.GeoJSON()).writeFeatures(select.getFeatures().getArray(), {
          featureProjection: 'EPSG:3857'
        });
        var total_surface = JSON.parse(geojson).features.reduce(function(initial, feature) {
          return initial + turf.area(feature);
        }, 0);
        if (total_surface > 0) {
          surface.innerHTML = 'The selected area is ' + Math.round(total_surface) + ' square meters';
        } else {
          surface.innerHTML = not_selected;
        }
      });
      map.addInteraction(select);

      fetch('assets/data/quartiers_nantes.geojson').then(function(response) {
        return response.json().then(function(polys_fc) {
          console.log(polys_fc);
          vectorSourcePolygons.addFeatures(geojsonToFeatures(polys_fc, {
            featureProjection: 'EPSG:3857'
          }));
        });
      });

measurement/bboxPolygon.html.body:
turf-bboxPolygon
Takes a bbox and returns the equivalent polygon feature.













    Turf and OpenLayers 3 - BboxPolygon




      Generate random bboxpolygon from existing area


      function getRandomInt(min, max) {
        return Math.floor(Math.random() * (max - min + 1)) + min;
      }
      // Declare a source for polygons
      var vectorSourcePolygons = new ol.source.Vector();

      var vectorLayerPolygons = new ol.layer.Vector({
        source: vectorSourcePolygons,
        style: [
          new ol.style.Style({
            stroke: new ol.style.Stroke({
              color: [0, 121, 88, 1],
              width: 2
            })
          })
        ]
      });

      // Declare a vector layer for bboxPolygon
      var vectorLayerBboxPolygon = new ol.layer.Vector({
        source: new ol.source.Vector(),
        style: [
          new ol.style.Style({
            stroke: new ol.style.Stroke({
              color: [0, 0, 0, 1],
              width: 2
            })
          })
        ]
      });

      // Instanciate a map and add layers
      var map = new ol.Map({
        target: 'map',
        layers: [
          new ol.layer.Tile({
            source: new ol.source.OSM()
          }),
          vectorLayerPolygons,
          vectorLayerBboxPolygon
        ],
        view: new ol.View({
          center: ol.proj.transform(
            [-1.5603, 47.2383],
            'EPSG:4326',
            'EPSG:3857'
          ),
          zoom: 11
        })
      });

      fetch('assets/data/quartiers_nantes.geojson').then(function(response) {
        return response.json().then(function(polys_fc) {
          console.log(polys_fc);

          vectorSourcePolygons.addFeatures(geojsonToFeatures(polys_fc, {
            featureProjection: 'EPSG:3857'
          }));

          // Create a button and bind a click function
          var button = document.getElementById('createbboxpolygon');
          var draw;
          button.addEventListener('click', function(e) {
            var source = vectorLayerBboxPolygon.getSource();
            source.clear();
            // Random feature extraction
            var features = vectorLayerPolygons.getSource().getFeatures();
            var feat = features[getRandomInt(0, features.length - 1)];
            // Get extent
            var featureExtent = ol.proj.transformExtent(feat.getGeometry().getExtent(), 'EPSG:3857', 'EPSG:4326');
            // Create a GeoJSON reusing the extent
            var featureBboxPolygon = turf.bboxPolygon(featureExtent);
            source.addFeatures(geojsonToFeatures(featureBboxPolygon, {
               featureProjection: 'EPSG:3857'
            }));
          });
        });
      });

measurement/bearing.html.body:
turf-bearing
Calculates the bearing between two point features in degrees.










    Turf and OpenLayers 3 - bearing






      var x1 = -1.5741021;
      var y1 = 47.1991929;
      // Cranes of Nantes with properties defined
      var pt1 = turf.point([x1, y1], {
        name: 'Grue grise',
        man_made: 'crane'
      });

      var x2 = -1.57007;
      var y2 = 47.20507;
      var pt2 = turf.point([x2, y2], {
        name: 'Grue jaune',
        man_made: 'crane'
      });

      var fc = turf.featureCollection([pt1, pt2]);

      var bearing = turf.bearing(pt1, pt2);

      // Declare a source
      var vectorSource = new ol.source.Vector();

      // When reading feature, reproject to EPSG 3857
      var features = (new ol.format.GeoJSON()).readFeatures(fc, {
        featureProjection: 'EPSG:3857'
      });
      // Add a feature
      vectorSource.addFeatures(features);

      var pointsStyle = [new ol.style.Style({
        image: new ol.style.Circle({
          stroke: new ol.style.Stroke({
            color: 'white'
          }),
          fill: new ol.style.Fill({
            color: '#1f6b75'
          }),
          radius: 5
        })
      })];

      // Declare a vector layer with the already
      // created source containing added features
      var vectorLayer = new ol.layer.Vector({
        source: vectorSource,
        style: pointsStyle
      });

      var vectorLine = new ol.layer.Vector({
        source: new ol.source.Vector({
          features: (new ol.format.GeoJSON()).readFeatures(
            turf.lineString([[x1, y1], [x2, y2]], {}), {
              featureProjection: 'EPSG:3857'
            }
          ),
          projection: 'EPSG:3857'
        }),
        style: [
          new ol.style.Style({
            stroke: new ol.style.Stroke({
              color: 'white',
              width: 3
            })
          }),
          new ol.style.Style({
            stroke: new ol.style.Stroke({
              color: [0, 121, 88, 1],
              width: 1
            })
          })
        ]
      });

      // Instanciate a map and add layers
      var map = new ol.Map({
        target: 'map',
        layers: [
          new ol.layer.Tile({
            source: new ol.source.OSM()
          }),
          vectorLine,
          vectorLayer
        ],
        view: new ol.View({
          center: ol.proj.transform(
            [-1.571456, 47.202561],
            'EPSG:4326',
            'EPSG:3857'
          ),
          zoom: 15
        })
      });

      var bearingElement = document.getElementById('bearing');
      bearingElement.innerHTML = Math.round(bearing * 1000) / 1000;

